import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {listMyOrders} from '../actions/orderActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import {SecondaryNavbar} from "../components/Navbar/secondaryNavbar";
import {detailsUser} from "../actions/userActions";
import {
    Container,
    Form,
    FormButton,
    FormContent,
    FormH1,
    FormInput,
    FormLabel,
    FormWrap,
    Icon, Text
} from "../components/Login/LoginElements";
import {FaArrowLeft} from "react-icons/all";

export default function ProfilePage(props) {

    const userSignin = useSelector(state => state.userSignin);
    const {userInfo} = userSignin;
    const myOrdersList = useSelector(state => state.myOrdersList);
    const { loading, error, orders } = myOrdersList;
    const userDetails  = useSelector(state => state.userDetails);
    const { loading: userLoading, error: userError, user } = userDetails;
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(listMyOrders());
        dispatch(detailsUser(userInfo._id));
    }, [dispatch, userInfo._id]);


    const submitHandler = () => {
        //
    }

    return (
        <div className="grid-container">
            <SecondaryNavbar/>
            <h1>Historia Zamówień</h1>
            <div>
                {loading ? (<LoadingBox/>) : error ? (<MessageBox variant="danger">{error}</MessageBox>) : (
                    <table className="table">
                        <thead>
                        <tr>
                            <th>NUMER</th>
                            <th>DATA</th>
                            <th>KWOTA</th>
                            <th>ZAPŁACONE</th>
                            <th>DOSTARCZONE</th>
                            <th>AKCJE</th>
                        </tr>
                        </thead>
                        <tbody>
                        {orders.map(order => (
                            <tr key={order._id}>
                                <td>{order._id}</td>
                                <td>{order.createdAt.substring(0, 10)}</td>
                                <td>{order.totalPrice.toFixed(2)} zł</td>
                                <td>{order.isPaid ? order.paidAt.substring(0, 10) : 'Nieopłacone'}</td>
                                <td>{order.isDelivered ? order.deliveredAt.substring(0, 10) : 'Niedostarczone'}</td>
                                <td><button type="button" className="small" onClick={() => {props.history.push(`/order/${order._id}`);}}>Szczegóły</button>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                )}
                    <div><h1>Profil użtykownika</h1></div>
                    {
                        userLoading ? <LoadingBox/> : userError ? <MessageBox variant="danger">{error}</MessageBox> : (
                            <Container>
                                <FormWrap>
                                    <Icon to="/"><FaArrowLeft/></Icon>

                                    <FormContent>
                                        <Form onSubmit={submitHandler} action="/">
                                            <FormLabel htmlFor='for'>Imię i Nazwisko</FormLabel>
                                            <FormInput type='text' id="name" placeholder="Wprowadź Imię i Nazwisko" value={user.name}/>
                                            <FormLabel htmlFor='for'>Hasło</FormLabel>
                                            <FormInput type='password' id="password" placeholder="Wprowadź Hasło" required onChange={e => setPassword(e.target.value)}/>
                                            {loading && <LoadingBox/>}
                                            {error && <MessageBox variant="danger">{error}</MessageBox>}
                                            <FormButton type='submit'>Zaloguj</FormButton>
                                            <Text><Link to="/recover" style={{color: 'white'}} className="hoverred">Przypomnij Hasło </Link></Text>
                                            <Text>Nie posiadasz konta? <Link to={`/register?redirect=${redirect}`} style={{color: 'white'}} className="hoverred">Stwórz je! </Link></Text>
                                        </Form>
                                    </FormContent>
                                </FormWrap>
                            </Container>
                        )
                    }
            </div>
        </div>

    );
}
