import React from 'react'
import {
    MobileIcon,
    Nav,
    NavbarContainer,
    NavBtn,
    NavBtnLink,
    NavLogo,
} from "./SecondaryNavbarElements";
import {FaBars} from "react-icons/all";
import {IconContext} from "react-icons/lib";
import {useDispatch, useSelector} from "react-redux";
import {NavLogoImg} from "./NavbarElements";
import plex from "../../images/plex to edit.png";
import whiteplex from "../../images/whiteplex.png";
import {signout} from "../../actions/userActions";

export const SecondaryNavbar = ({toggle}) => {

    const cart = useSelector(state => state.cart);
    const {cartItems} = cart;
    const userSignin = useSelector(state => state.userSignin);
    const {userInfo} = userSignin;
    const dispatch = useDispatch();


    const signoutHandler = () => {
        dispatch(signout());
    }



    return (
        <>
            <IconContext.Provider value={{color: '#fff'}}>
                <Nav>
                    <NavbarContainer>
                        <NavLogo to="/" className="swap-on-hover">
                            <NavLogoImg src={plex} id="navLogo" className="swap-on-hover__front-image"/>
                            <NavLogoImg src={whiteplex} id="navLogo" className="swap-on-hover__back-image"/>
                        </NavLogo>
                        <MobileIcon onClick={toggle}>
                            <FaBars />
                        </MobileIcon>
                        <NavBtn>
                            <NavBtnLink to="/cart">Koszyk
                                {cartItems.length > 0 && (
                                    <span className="badge">{cartItems.length}</span>
                                )}
                            </NavBtnLink>

                            {userInfo ? (
                                    <NavBtnLink to="/profile">Moje Konto ({userInfo.name})</NavBtnLink>)
                                : (<NavBtnLink to="/login">Zaloguj się</NavBtnLink>)}
                            {userInfo && (<NavBtnLink to="/" onClick={signoutHandler}>Wyloguj się</NavBtnLink>)}

                        </NavBtn>
                    </NavbarContainer>
                </Nav>
            </IconContext.Provider>
        </>
    )
}
