import React from 'react'
import {useDispatch, useSelector} from "react-redux";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import {useEffect} from "react";


const ProfilePage = (props) => {

    const myOrders = useSelector(state => state.myOrders);
    const {loading, error, orders} = myOrders;
    const dispatch = useDispatch();

    useEffect(() => {

    })

    return (
        <div>
            <h1>Historia Zamówień</h1>
            {loading ? <LoadingBox/>:error? <MessageBox variant="danger">{error}</MessageBox>: (
                <table className="table">
                    <thead>
                        <tr>
                            <th>NUMER</th>
                            <th>DATA UTWORZENIA</th>
                            <th>RACHUNEK</th>
                            <th>ZAPŁACONO</th>
                            <th>DOSTARCZONO</th>
                            <th>AKCJE</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            orders.map(order => (
                                <tr key={order._id}>
                                    <td>{order.createdAt}</td>
                                    <td>{order.totalPrice} zł</td>
                                    <td>{order.isPaid ? order.paidAt.substring(0, 10) : 'Nieopłacone'}</td>
                                    <td>{order.isDelivered ? order.deliveredAt.substring(0, 10) : 'Niedostarczone'}</td>
                                    <td><button type="button" className="small" onClick={() => {props.history.push(`/order/${order._id}`)}}>Szczegóły</button></td>
                                </tr>
                            ))
                        }
                    </tbody>
                </table>
            )}
        </div>
    );
}

export default ProfilePage
