import {useDispatch, useSelector} from "react-redux";
import {createProduct, listProducts} from "../actions/productActions";
import {useEffect} from "react";
import LoadingBoxWhite from "../components/LoadingBoxWhite";
import MessageBox from "../components/MessageBox";
import {SecondaryNavbar} from "../components/Navbar/secondaryNavbar";

export default function ProductListPage(props) {
    const productCreate = useSelector(state => state.productCreate);
    const {loading: loadingCreate, error: errorCreate, success: successCreate, product: createdProduct} = productCreate;
    const productList = useSelector(state => state.productList);
    const {loading, error, products} = productList;
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(listProducts());
    }, [dispatch]);

    const deleteHandler = () => {
       //TODO
    }

    const createHandler = () => {
        dispatch(createProduct());
    }

    return (
      <div className="grid-container">
          <SecondaryNavbar/>

              <div className="row">
                  <h1 style={{margin: '10px'}}>Produkty</h1>
                  <button className="primary" type="button" onClick={createHandler}>Utwórz Produkt</button>
              </div>
              {
                  loading ? <LoadingBoxWhite/>:
                      error ? <MessageBox variant="danger">{error}</MessageBox>:
                          <table className="table">
                              <thead>
                              <tr>
                                  <th>NUMER</th>
                                  <th>NAZWA</th>
                                  <th>CENA</th>
                                  <th>KATEGORIA</th>
                                  <th>MARKA</th>
                                  <th>AKCJE</th>
                              </tr>
                              </thead>
                              <tbody>
                              {
                                  products.map((product) => (
                                      <tr key={product._id}>
                                          <td>{product._id}</td>
                                          <td>{product.name}</td>
                                          <td>{product.price}</td>
                                          <td>{product.category}</td>
                                          <td>{product.brand}</td>
                                          <td>
                                              <button type="button" className="small" onClick={() => props.history.push(`/product/${product._id}/edit`)}>Edytuj</button>
                                              <button type="button" className="small" onClick={() => deleteHandler(product)}>Usuń</button>
                                          </td>
                                      </tr>
                                  ))
                              }
                              </tbody>

                          </table>
              }
      </div>
    );
}
