import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {listMyOrders} from '../actions/orderActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import {SecondaryNavbar} from "../components/Navbar/secondaryNavbar";
import {detailsUser, updateUserProfile} from "../actions/userActions";
import {
    Container,
    Form,
    FormButton,
    FormContent,
    FormH1,
    FormInput,
    FormLabel,
    FormWrap,
    Icon, Text
} from "../components/Login/LoginElements";
import {Button, ButtonS} from "../components/ButtonElement";
import {useState} from "react";

export default function ProfilePage(props) {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const userSignin = useSelector(state => state.userSignin);
    const {userInfo} = userSignin;
    const myOrdersList = useSelector(state => state.myOrdersList);
    const { loading, error, orders } = myOrdersList;
    const userDetails  = useSelector(state => state.userDetails);
    const { loading: userLoading, error: userError, user } = userDetails;
    const userUpdateProfile  = useSelector(state => state.userUpdateProfile);
    const {success, error: updateError, loading: updateLoading} = userUpdateProfile;
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(listMyOrders());
        if(!user){
            dispatch(detailsUser(userInfo._id));
        } else {
            setName(user.name);
            setEmail(user.email);
        }
    }, [dispatch, userInfo._id, user]);


    const submitHandler = (e) => {
        e.preventDefault();
        if(password !== confirmPassword){
            alert('Hasła muszą być takie same!');
        } else {
            dispatch(updateUserProfile({userId: user._id, name, email, password}));
        }
    }

    return (
        <div className="grid-container">
            <SecondaryNavbar/>
            <h1>Historia Zamówień</h1>
            <div>
                {loading ? (<LoadingBox/>) : error ? (<MessageBox variant="danger">{error}</MessageBox>) : (
                    <table className="table">
                        <thead>
                        <tr>
                            <th>NUMER</th>
                            <th>DATA</th>
                            <th>KWOTA</th>
                            <th>ZAPŁACONE</th>
                            <th>DOSTARCZONE</th>
                            <th>AKCJE</th>
                        </tr>
                        </thead>
                        <tbody>
                        {orders.map(order => (
                            <tr key={order._id}>
                                <td>{order._id}</td>
                                <td>{order.createdAt.substring(0, 10)}</td>
                                <td>{order.totalPrice.toFixed(2)} zł</td>
                                <td>{order.isPaid ? order.paidAt.substring(0, 10) : 'Nieopłacone'}</td>
                                <td>{order.isDelivered ? order.deliveredAt.substring(0, 10) : 'Niedostarczone'}</td>
                                <td><button type="button" className="small" onClick={() => {props.history.push(`/order/${order._id}`);}}>Szczegóły</button>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                )}
                {
                    userLoading ? <LoadingBox/> : userError ? <MessageBox variant="danger">{error}</MessageBox> : (
                        <div className="container">

                            <FormWrap>
                                <FormContent>
                                    <Form onSubmit={submitHandler} action="/">
                                        <FormH1>Profil Użytkownika</FormH1>
                                        <FormLabel htmlFor='for'>Imię i Nazwisko</FormLabel>
                                        <FormInput type='text' id="name" placeholder="Wprowadź Imię i Nazwisko" value={name} required onChange={e => setName(e.target.value)}/>
                                        <FormLabel htmlFor='for'>Email</FormLabel>
                                        <FormInput type='text' id="email" placeholder="Wprowadź Email" value={email}  required onChange={e => setEmail(e.target.value)}/>
                                        <FormLabel htmlFor='for'>Hasło</FormLabel>
                                        <FormInput type='password' id="password" placeholder="Wprowadź Hasło"  required onChange={e => setPassword(e.target.value)}/>
                                        <FormLabel htmlFor='for'>Powtórz Hasło</FormLabel>
                                        <FormInput type='password' id="confirmPassword" placeholder="Powtórz Hasło"  required onChange={e => setConfirmPassword(e.target.value)}/>
                                        <Button primary="true" secondary="true" light="true" type="submit">
                                            Edytuj Profil
                                        </Button>
                                    </Form>
                                </FormContent>
                            </FormWrap>
                        </div>
                    )
                }
            </div>
        </div>

    );
}
